mapper.select:
SELECT COUNT(1) FROM `building_correction` WHERE `system_building_id` IS NULL
    AND `processed` = 0 AND `idjek` IN 

        #{idjek}

SELECT DISTINCT b.`object_id` FROM `building` b JOIN `building_address` addr ON (((b.`parent_id` = addr.`object_id`) OR EXISTS(SELECT 1 FROM `building_attribute` ba WHERE ba.`object_id` = b.`object_id` AND ba.`status` = 'ACTIVE' AND ba.`attribute_type_id` = #{buildingBuildingAddressAT} AND ba.`value_id` = addr.`object_id`)) AND addr.`status` = 'ACTIVE') JOIN `building_address_attribute` num ON (num.`object_id` = addr.`object_id` AND num.`status` = 'ACTIVE' AND num.`attribute_type_id` = #{buildingAddressNumberAT}) JOIN `building_address_string_culture` num_sc ON (num_sc.`id` = num.`value_id`) LEFT JOIN `building_address_attribute` corp ON (corp.`object_id` = addr.`object_id` AND corp.`status` = 'ACTIVE' AND corp.`attribute_type_id` = #{buildingAddressCorpAT}) LEFT JOIN `building_address_string_culture` corp_sc ON (corp_sc.`id` = corp.`value_id`)
WHERE b.`status` IN ('ACTIVE', 'INACTIVE') AND num_sc.`value` = #{dom} AND
corp_sc.`value` IS NULL
corp_sc.`value` = #{corp}
AND addr.`parent_entity_id` = #{parentEntityId} AND addr.`parent_id` = #{parentId}
