mapper.select:
SELECT e.`object_id` FROM `apartment_card` e JOIN `apartment_card_attribute` address ON (address.`object_id` = e.`object_id` AND address.`attribute_type_id` = #{apartmentCardAddressAT} AND address.`status` = 'ACTIVE' AND address.`value_id` = #{addressId} AND address.`value_type_id` = #{addressTypeId}) JOIN `apartment_card_attribute` owner ON (owner.`object_id` = e.`object_id` AND owner.`attribute_type_id` = #{apartmentCardOwnerAT} AND owner.`status` = 'ACTIVE' AND owner.`value_id` = #{ownerId}) WHERE e.`status` = 'ACTIVE'
AND e.`object_id` != #{apartmentCardId}
LIMIT 0,1

SELECT MAX(d.`date`) FROM (
    SELECT a.`start_date` `date` FROM `apartment_card_attribute` a WHERE a.`object_id` = #{apartmentCardId} 
        AND a.`start_date`  <  #{date}
    UNION 
    SELECT ra.`start_date` FROM `registration_attribute` ra WHERE ra.`start_date`  <  #{date}
        AND ra.`object_id` IN (SELECT aca.`value_id` FROM `apartment_card_attribute` aca WHERE aca.`object_id` = #{apartmentCardId}
            AND aca.`start_date`  <  #{date} AND aca.`attribute_type_id` = #{apartmentCardRegisrationAT})
    UNION
    SELECT r.`end_date` FROM `registration` r WHERE r.`end_date`  <  #{date} 
        AND r.`object_id` IN (SELECT aca.`value_id` FROM `apartment_card_attribute` aca WHERE aca.`object_id` = #{apartmentCardId}
            AND aca.`start_date`  <  #{date} AND aca.`attribute_type_id` = #{apartmentCardRegisrationAT})
) d

SELECT MIN(d.`date`) FROM (
    SELECT a.`start_date` `date` FROM `apartment_card_attribute` a WHERE a.`object_id` = #{apartmentCardId} 
        AND a.`start_date`  >  #{date}
    UNION 
    SELECT ra.`start_date` FROM `registration_attribute` ra WHERE ra.`start_date`  >  #{date}
        AND ra.`object_id` IN (SELECT aca.`value_id` FROM `apartment_card_attribute` aca WHERE aca.`object_id` = #{apartmentCardId}
            AND aca.`attribute_type_id` = #{apartmentCardRegisrationAT})
    UNION
    SELECT r.`end_date` FROM `registration` r WHERE r.`end_date`  >  #{date} 
        AND r.`object_id` IN (SELECT aca.`value_id` FROM `apartment_card_attribute` aca WHERE aca.`object_id` = #{apartmentCardId}
            AND aca.`attribute_type_id` = #{apartmentCardRegisrationAT})
) d
