mapper.sql:
WHERE a.`status` = 'ACTIVE' AND a.`parent_entity_id` = 500 AND a.`parent_id` = #{buildingId}

    AND a.`permission_id` IN ${apartmentPermissionString}


    AND ap_name.`value` = #{number}

WHERE r.`status` = 'ACTIVE' AND r.`parent_entity_id` = 500 AND r.`parent_id` = #{buildingId}

    AND r.`permission_id` IN ${roomPermissionString}


    AND room_name.`value` = #{number}

mapper.select:
SELECT (SELECT COUNT(DISTINCT a.`object_id`) FROM `apartment` a


) +
(SELECT COUNT(DISTINCT r.`object_id`) FROM `room` r


) FROM DUAL

SELECT apartmentsAndRooms.`objectId`, apartmentsAndRooms.`entity` FROM ( SELECT DISTINCT a.`object_id` `objectId`, 'apartment' `entity`, '1' `prevalence`, ap_sort_name.`value` `value_sort` FROM `apartment` a
UNION SELECT DISTINCT r.`object_id`, 'room', '2', room_sort_name.`value` FROM `room` r
) apartmentsAndRooms ORDER BY apartmentsAndRooms.`prevalence`, LPAD(apartmentsAndRooms.`value_sort`, 15, ' ')
LIMIT ${start},${size}
