mapper.sql:
JOIN `apartment_attribute` aa ON (aa.`status` = 'ACTIVE' AND aa.`attribute_type_id` = ${apartmentNameAT}
    AND aa.`object_id` = a.`object_id`)
JOIN `apartment_string_culture` ap_name ON (ap_name.`id` = aa.`value_id`)

JOIN `room_attribute` ra ON (ra.`status` = 'ACTIVE' AND ra.`attribute_type_id` = ${roomNameAT}
    AND ra.`object_id` = r.`object_id`)
JOIN `room_string_culture` room_name ON (room_name.`id` = ra.`value_id`)

WHERE a.`status` = 'ACTIVE' AND a.`parent_entity_id` = 500 AND a.`parent_id` = #{buildingId}

    AND a.`permission_id` IN ${apartmentPermissionString}


    AND ap_name.`value` = #{number}

WHERE r.`status` = 'ACTIVE' AND r.`parent_entity_id` = 500 AND r.`parent_id` = #{buildingId}

    AND r.`permission_id` IN ${roomPermissionString}


    AND room_name.`value` = #{number}

mapper.select:
SELECT (SELECT COUNT(DISTINCT a.`object_id`) FROM `apartment` a


) +
(SELECT COUNT(DISTINCT r.`object_id`) FROM `room` r


) FROM DUAL

SELECT apartmentsAndRooms.`objectId`, apartmentsAndRooms.`entity` FROM (
        SELECT DISTINCT a.`object_id` `objectId`, 'apartment' `entity` FROM `apartment` a 


        UNION
        SELECT DISTINCT r.`object_id`, 'room' FROM `room` r 


    ) apartmentsAndRooms 

LIMIT ${start},${size}
